import "./styles.scss";
import { Link, Route, Routes } from "react-router-dom"; //BrowserRouter isn't required because it exists in index.js
import HomePage from "./components/HomePage";
import ViewAppointment from "./components/ViewAppointment";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Login from "./components/Login";
import Registration from "./components/Registration";
import CreateAppointment from "./components/CreateAppointment";
import UserButton from "./components/UserButton";
import ViewMedRecord from "./components/ViewMedRecord";
import PatientRecords from "./components/PatientRecords";
import UserChoice from "./components/UserChoice";
import { useState } from "react";

/*

These 3 constants, header links, labels and routes are all used for navigration, if you edit an index of one you are required to edit the same index of the rest.
For example: if index 1 of links is to login, then index 1 of labels must display a Login label, and index 1 of routes must route to a login component.

*/

let headerLinks = [""];
let headerLabels = [""];
let headerRoutes: React.ReactElement[] = [];

/*

const headerLinks = ["/", "/login", "/reg", "/viewApp", "/createApp"]; //This array contains the paths to elements
const headerLabels = [
  "Home",
  "Login",
  "Registration",
  "View and Cancel Appointments",
  "Create Appointment",
]; //This array contains the labels of the header links
const headerRoutes: React.ReactElement[] = [
  <HomePage />,
  <Login />,
  <Registration />,
  <ViewAppointment />,
  <CreateAppointment />,
]; //This array contains the elements to be routed to

*/

let routeCreator: React.ReactElement[] = []; //This is array is used to store our route componenets, which are later put on the site

function App() {
  interface Temp {
    headerLinksI: string[];
    headerLabelsI: string[];
    headerRoutesI: React.ReactElement[];
  }

  const [header, setHeader] = useState<Temp>({
    headerLinksI: headerLinks,
    headerLabelsI: headerLabels,
    headerRoutesI: headerRoutes,
  });

  const createFinalRoutes = () => {
    //This if statement generates the amount of routes required based on the amount of labels and links to page components.
    if (headerLinks.length > 0 && headerLabels.length > 0) {
      for (let i = 0; i < headerLinks.length; i++) {
        routeCreator.push(
          <Route
            path={header.headerLinksI[i]}
            element={header.headerRoutesI[i]}
          />
        );
      }
    }

    setHeader({
      headerLinksI: headerLinks,
      headerLabelsI: headerLabels,
      headerRoutesI: headerRoutes,
    });
  };

  const createLinks = (links: string[]) => {
    for (let i = 0; i < links.length; i++) {
      headerLinks.push(links[i]);
    }
  };

  const createLabels = (labels: string[]) => {
    for (let i = 0; i < labels.length; i++) {
      headerLabels.push(labels[i]);
    }
  };

  const createRoutes = (routes: React.ReactElement[]) => {
    for (let i = 0; i < routes.length; i++) {
      headerRoutes.push(routes[i]);
    }
  };

  //This is intended to handle the user choice
  const [isVisible, setIsVisible] = useState<boolean>(true);
  const [message, setMessage] = useState("");

  const handleNavigation = (input: string) => {
    setMessage(input);
    //setIsVisible(false);
  };

  return (
    <>
      <div id="header">
        <Header links={header.headerLinksI} labels={header.headerLabelsI} />
      </div>

      <div className="govuk-width-container app-width-container" id="body">
        <Routes>{routeCreator};</Routes>

        {(() => {
          switch (message) {
            case "Patient":
              return (
                <Login
                  userType="Patient"
                  passLabels={createLabels}
                  passLinks={createLinks}
                  passRoutes={createRoutes}
                  onClick={createFinalRoutes}
                />
              );
            case "Doctor":
            //return <Login userType="Doctor" />;
            case "Admin":
            //return <Login userType="Admin" />;
            default:
              return <UserChoice onButClick={handleNavigation} />;
          }
        })()}
      </div>

      <div id="footer">
        <Footer />
      </div>
      <script src="/govuk-frontend/all.js"></script>
    </>
  );
}

export default App;
